package Lista06;

import java.util.Scanner;

public class Q12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner in = new Scanner(System.in);
		
		double ngraos = 1;
		int y = 64;
		
		for (int i = 1; i <= 1; i++) {
			ngraos = ngraos * 1;
			System.out.println("soma " +i+ " graos " +ngraos);
		}
		
		for (int i = 2; i <= y; i++) {
			ngraos = ngraos * 2;
			System.out.println("soma " +i+ " graos " +ngraos);
		}
			
		in.close();
		
			

	}

}
